            template <typename THandler, typename TBuilder, typename... TArgs>
            inline void add_basic(TBuilder& builder, const TArgs&... args) noexcept {
                (void)std::initializer_list<int>{
                    (THandler::set_value(builder.object(), args), 0)...
                };
            }

